import React from "react";
import DropDownComponent from "./DropDownComponent";

// props
// name: student name
// question: students question
export default class Ticket extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      teacherOptions: false,
      optionsList: [{name:"accept", text:"Accept", active:true, onClick: ()=>{console.log("test")}}], //{name:"", text:"", active:false/true, onClick:()=>{}}
    };
  }

  render() {
    return (
      <div className="questionWindow">
        <div className="leftContainer">
          <div className="nameHolder">{this.props.name}</div>
          {this.state.teacherOptions ? <DropDownComponent active={true} name="teacherOptions" options={this.state.optionsList}/> : <div>false</div>}
        </div>
        <div className="questionHolder">{this.props.question}</div>
      </div>
    );
  }
}
