import React from "react";


export default class PasswordReset extends React.Component {
    constructor(props){
        super(props);
        
        this.state = {
            email: "",
            newPass: "",
            confirmPass: "",
        };
    }

    updateEmail = (event) => {
        this.setState({email: event.target.value});
    }

    updatePass = (event) => {
        this.setState({newPass: event.target.value});
    }

    updateConfirmPass = (event) => {
        this.setState({confirmPass: event.target.value});
    }

    render() {
        return(
            <div className="pageContainer">
                <div className="passWindowContainer">
                    <div className="passWindow">
                        <p className="resetLabel">Password Reset</p>
                        <p className="emailLabel">Account Email:</p>
                        <input type="email" className="emailInput" placeholder="Account Email..."/>
                        <p className="passwordLabel">New Password:</p>
                        <input type="password" className="passwordInput" placeholder="New Password..."/>
                        <p className="confirmPasswordLabel">Confirm New Password:</p>
                        <input type="password" className="confirmPasswordInput" placeholder="Confirm Password..."/>
                    </div>
                </div>
            </div>
        );
    }
}
