import React from "react";
import Button from "./RouteButton";

export default class StudentOption extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      selected: false,
    };
  }

  setSelected = () => {
    this.setState({selected: !this.state.selected});
  }

  render() {
    return (
      <div className={this.state.selected ? "studentOption selectedOption" : "studentOption"}>
        <div className="studentName">{this.props.name}</div>
        <div className="buttonContainer">
          <Button
            active={true}
            text={"Select"}
            onclick={this.setSelected}
            buttonType={this.state.selected ? "selectedButton" : "selectButton"}
          />
        </div>
      </div>
    );
  }
}
