import React from "react";
import DropDownMenu from "./DropDownMenu";

// props:
// active: whether the component is active
// name: class name of the component
// options: list of kvs that will be turned into DropDownOptions
export default class DropDownComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        isDropped : false,
    }
  }

  dropPressed = () => {
      this.setState({isDropped: !this.state.isDropped})
  }

  render() {
    return (
      <div
        className={
          this.props.name + " dropcontainer " + this.props.active
            ? "active"
            : "inactive"
        }
      >
        <div className="dropComponent">
            <DropDownMenu active={true} options={this.props.options} name="TAOptions" visible={this.state.isDropped}/>
        </div>
      </div>
    );
  }
}
